   function form($instance) {
        $title = isset($instance[ 'title' ])? $instance[ 'title' ] : '' ;
        $adress = isset($instance[ 'adress' ])? $instance[ 'adress' ] : '' ;
        $phoneNumber = isset($instance[ 'phoneNumber' ])? $instance[ 'phoneNumber' ] : '' ;
        $worktime = isset($instance[ 'worktime' ])? $instance[ 'worktime' ] : '' ;
        $email = isset($instance[ 'email' ])? $instance[ 'email' ] : '' ; ?>

        <p>
            <label for="<?php echo $this->get_field_id('title'); ?>"><?php _e('Title:'); ?>
                <input class="widefat" id="<?php echo $this->get_field_id('title'); ?>"
                       name="<?php echo $this->get_field_name('title'); ?>" type="text" value="<?php echo $title; ?>"/>
            </label>
        </p>
        <p>
        <label for="<?php echo $this->get_field_id('adress'); ?>"><?php _e('Adress:'); ?>
            <input class="widefat" id="<?php echo $this->get_field_id('adress'); ?>"
                   name="<?php echo $this->get_field_name('adress'); ?>" type="text"
                   value="<?php echo $adress; ?>"/>
        </label>
        </p>
        <p>
            <label for="<?php echo $this->get_field_id('phoneNumber'); ?>"><?php _e('Phone Number:'); ?>
                <input class="widefat" id="<?php echo $this->get_field_id('phoneNumber'); ?>"
                       name="<?php echo $this->get_field_name('phoneNumber'); ?>" type="text"
                       value="<?php echo $phoneNumber; ?>"/>
            </label>
        </p>
        <p>
            <label for="<?php echo $this->get_field_id('worktime'); ?>"><?php _e('worktime '); ?>
                <input class="widefat" id="<?php echo $this->get_field_id('worktime'); ?>"
                       name="<?php echo $this->get_field_name('worktime'); ?>" type="text" value="<?php echo $worktime; ?>"/>
            </label>
        </p>
        <p>
            <label for="<?php echo $this->get_field_id('email'); ?>"><?php _e('E-mail:'); ?>
                <input class="widefat" id="<?php echo $this->get_field_id('email'); ?>"
                       name="<?php echo $this->get_field_name('email'); ?>" type="text" value="<?php echo $email; ?>"/>
            </label>
        </p>
        <?php
    }
